actions.query:
mutation ($id: String!, $version: Long!, $locale: Locale!) {
  createMyOrderFromCart(draft: {id: $id, version: $version}) {
    ...OrderFields
    __typename
  }
}

fragment OrderFields on Order {
  id
  orderNumber
  createdAt
  lineItems {
    id
    name(locale: $locale)
    productSlug(locale: $locale)
    quantity
    price {
      value {
        ...MoneyFields
        __typename
      }
      discounted {
        value {
          ...MoneyFields
          __typename
        }
        __typename
      }
      __typename
    }
    totalPrice {
      ...MoneyFields
      __typename
    }
    variant {
      sku
      images {
        url
        __typename
      }
      attributesRaw {
        name
        value
        attributeDefinition {
          type {
            name
            __typename
          }
          name
          label(locale: $locale)
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
  totalPrice {
    ...MoneyFields
    __typename
  }
  shippingInfo {
    shippingMethod {
      name
      localizedDescription(locale: $locale)
      __typename
    }
    price {
      ...MoneyFields
      __typename
    }
    __typename
  }
  taxedPrice {
    totalGross {
      ...MoneyFields
      __typename
    }
    totalNet {
      ...MoneyFields
      __typename
    }
    __typename
  }
  discountCodes {
    discountCode {
      id
      code
      name(locale: $locale)
      __typename
    }
    __typename
  }
  shippingAddress {
    ...AddressFields
    __typename
  }
  billingAddress {
    ...AddressFields
    __typename
  }
  paymentInfo {
    payments {
      paymentStatus {
        interfaceCode
        __typename
      }
      __typename
    }
    __typename
  }
  __typename
}

fragment MoneyFields on Money {
  centAmount
  currencyCode
  fractionDigits
  __typename
}

fragment AddressFields on Address {
  firstName
  lastName
  streetName
  additionalStreetInfo
  postalCode
  city
  country
  phone
  email
  __typename
}
